INSERT INTO `avaliacao` (`nome`, `descricao`, `requisito_adicional`, `submissao_inicial`, `submissao_final`, `correcao_inicial`, `correcao_final`, `num_correcao_estudante`, `nota_maxima`, `criterio_correcao`, `turma_id`) VALUES ('Sequência inversa', 'Escreva um algoritmo que coloque os números de 1 a 100 na tela na\r\nordem inversa (começando em 100 e terminando em 1). ', '*Linguagem C++\r\n*Utlizar estrutura de repetição \'for\'', '2016-10-25 00:00:00', '2016-10-28 23:59:00', '2016-10-29 00:00:00', '2016-11-02 23:59:00', 2, 100, '20 - Legibilidade do código.\r\n20 - Elegância do código.\r\n20 - Lógica de programação.\r\n20 - Complexidade do algoritmo.\r\n20 - Indentação.', 1);
INSERT INTO `avaliacao` (`nome`, `descricao`, `requisito_adicional`, `submissao_inicial`, `submissao_final`, `correcao_inicial`, `correcao_final`, `num_correcao_estudante`, `nota_maxima`, `criterio_correcao`, `turma_id`) VALUES ('Converter temperatura', 'Faça um algoritmo que apresente na tela a tabela de conversão de\r\ngraus Celsius para Fahrenheit, de -100 C a 100 C. Use um incremento de 10 C. Observação: Fahrenheit = (9 / 5) × Celsius + 32', '*Linguagem C++.', '2016-10-26 00:00:00', '2016-10-28 23:59:00', '2016-10-29 00:00:00', '2016-10-31 23:59:00', 2, 100, '20 - Legibilidade do código.\r\n20 - Elegância do código.\r\n20 - Lógica de programação.\r\n20 - Complexidade do algoritmo.\r\n20 - Indentação.', 1);
INSERT INTO `avaliacao` (`nome`, `descricao`, `requisito_adicional`, `submissao_inicial`, `submissao_final`, `correcao_inicial`, `correcao_final`, `num_correcao_estudante`, `nota_maxima`, `criterio_correcao`, `turma_id`) VALUES ('Múltiplos 7', 'Faça um algoritmo para listar todos os múltiplos positivos do número 7 menores ou iguais a 100.\r\n ', '*Linguagem C++.', '2016-10-26 00:00:00', '2016-10-28 23:59:00', '2016-10-29 00:00:00', '2016-10-31 23:59:00', 2, 100, '20 - Legibilidade do código.\r\n20 - Elegância do código.\r\n20 - Lógica de programação.\r\n20 - Complexidade do algoritmo.\r\n20 - Indentação.', 1);
INSERT INTO `avaliacao` (`nome`, `descricao`, `requisito_adicional`, `submissao_inicial`, `submissao_final`, `correcao_inicial`, `correcao_final`, `num_correcao_estudante`, `nota_maxima`, `criterio_correcao`, `turma_id`) VALUES ('Calcular o número h', 'Sendo h = 1 + 1/2 + 1/3 + 1/4 + ... + 1/N, construa um algoritmo para\r\ncalcular o número h, sendo o número inteiro N fornecido pelo usuário. Seu algoritmo deve garantir que apenas um valor maior do que zero seja aceito como entrada. ', '*Linguagem PHP || C#', '2016-10-26 00:00:00', '2016-10-28 23:59:00', '2016-10-29 00:00:00', '2016-10-31 23:59:00', 2, 100, '20 - Legibilidade do código.\r\n20 - Elegância do código.\r\n20 - Lógica de programação.\r\n20 - Complexidade do algoritmo.\r\n20 - Indentação.', 1);
INSERT INTO `avaliacao` (`nome`, `descricao`, `requisito_adicional`, `submissao_inicial`, `submissao_final`, `correcao_inicial`, `correcao_final`, `num_correcao_estudante`, `nota_maxima`, `criterio_correcao`, `turma_id`) VALUES ('Fatorial', 'Elabore um algoritmo (pseudocódigo) que calcule N! (fatorial de N), sendo que o valor de N é fornecido pelo usuário. Lembre que N é sempre um valor inteiro e não negativo.Logo, seu algoritmo deve evitar que valores negativos sejam aceitos como entrada. Lembre também que N! = 1 × 2 × 3 × ... × (N-1) × N, e que 0! = 1 por definição. ', '*Linguagem JAVA.', '2016-10-26 00:00:00', '2016-10-28 23:59:00', '2016-10-29 00:00:00', '2016-10-31 23:59:00', 2, 100, '20 - Legibilidade do código.\r\n20 - Elegância do código.\r\n20 - Lógica de programação.\r\n20 - Complexidade do algoritmo.\r\n20 - Indentação.', 1);
INSERT INTO `avaliacao` (`nome`, `descricao`, `requisito_adicional`, `submissao_inicial`, `submissao_final`, `correcao_inicial`, `correcao_final`, `num_correcao_estudante`, `nota_maxima`, `criterio_correcao`, `turma_id`) VALUES ('Soma de números', 'Faça um algoritmo que pergunte ao usuário quantos números deseja somar. Em seguida, leia a quantidade informada de números e apresentar o valor da soma, quantos números são maiores que 7 e quantos números são maiores que 9. ', '*Linguagem Ruby\r\n*Utilize Do-While', '2016-10-26 00:00:00', '2016-10-28 23:59:00', '2016-10-29 00:00:00', '2016-10-31 23:59:00', 2, 100, '20 - Legibilidade do código.\r\n20 - Elegância do código.\r\n20 - Lógica de programação.\r\n20 - Complexidade do algoritmo.\r\n20 - Indentação.', 1);